import React, { useState, useEffect } from "react";
import "./Dash.css";
import { ref, push, get, child } from "firebase/database"; // Update import for "once"
import { database } from "./firebase.js";

const chatRef = ref(database, "Chat");

function Dashboard() {
  const [inputText, setInputText] = useState("");
  const [messages, setMessages] = useState([]);

  const handleSendClick = () => {
    if (inputText.trim() !== "") {
      push(chatRef, {
        text: inputText,
        timestamp: Date.now(),
      });
      setInputText("");
    }
  };

  const handleNewMessage = (snapshot) => {
    const newMessage = snapshot.val();
    setMessages((prevMessages) => [newMessage, ...prevMessages]);
  };

  useEffect(() => {
    // Use "get" to retrieve initial data once
    get(child(chatRef)).then((snapshot) => {
      if (snapshot.exists()) {
        const data = snapshot.val();
        const messageArray = Object.values(data);
        setMessages(messageArray);
      }
    });
  }, []);

  return (
    <div className="chat-app-dashboard">
      <div className="blue-portion">
        <center>
          <img
            src="https://cdn3d.iconscout.com/3d/premium/thumb/young-boy-6877456-5638292.png?f=webp"
            alt=""
            style={{
              borderRadius: "100%",
              backgroundColor: "white",
              marginTop: "5%",
            }}
            height={120}
            width={120}
          />
          <h2 style={{ color: "white" }}>Arya</h2>
          <br />
          <hr style={{ color: "violet" }} />
        </center>
      </div>
      <div className="gray-portion">
        <div className="chat-messages">
          {messages.map((message, index) => (
            <div
              key={index}
              className={message.sender === "Arya" ? "sent-message" : "received-message"}
            >
              {message.text}
            </div>
          ))}
        </div>
        <div className="chat-input">
          <input
            type="text"
            placeholder="Type your message..."
            value={inputText}
            onChange={(e) => setInputText(e.target.value)}
          />
          <button onClick={handleSendClick}>Send</button>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
