import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./Dash.css";
import { database } from "./firebase"; // Assuming your Firebase initialization is done in "./firebase"
import { push, ref, onValue } from "firebase/database";

const chatRef = ref(database, "Chat");

function getRandomDarkColor() {
  // Generate a random dark color in hexadecimal format
  const letters = "0123456789ABCDEF";
  let color = "#";
  for (let i = 0; i < 6; i++) {
    color += letters[Math.floor(Math.random() * 16)];
  }
  return color;
}

function Dashboard({ user }) {
  const [inputText, setInputText] = useState("");
  const [messages, setMessages] = useState([]);

  const logout = () => {
    localStorage.clear();
    window.location.reload();
  };

  const handleSendMessage = (e) => {
    if ((e.key === "Enter" || e.type === "click") && inputText.trim() !== "") {
      const userColor = getRandomDarkColor();
      // Push a new message to the "Chat" reference
      push(chatRef, {
        text: inputText,
        timestamp: Date.now(),
        sender: "Aanchal",
        backgroundColor: userColor, // Store the user's color with the message
      }).then(() => {
        setInputText(""); // Clear the input field after sending a message
      });
    }
  };

  useEffect(() => {
    const unsubscribe = onValue(chatRef, (snapshot) => {
      const data = snapshot.val();
      if (data) {
        // Convert the object of messages into an array
        const messageArray = Object.values(data);
        setMessages(messageArray);
      }
    });
    return () => {
      unsubscribe();
    };
  }, []);

  return (
    <div className="home-container">
      <div id="a1">
        <img
          src="pro.jpg"
          alt=""
          style={{
            height: "20vh",
            width: "10vw",
            borderRadius: "100%",
            position: "fixed",
            top: "3%",
            left: "3%",
          }}
        />
        <div id="a2">
          <button className="chat-logout" onClick={logout}>
            <Link to="/" style={{ color: "white", textDecoration: "none" }}>
              Logout
            </Link>
          </button>
          <div className="chat-box" id="chatBox">
            {messages.map((message, index) => (
              <div
                key={index}
                className="message-box"
                style={{
                  color: message.backgroundColor, // Set background color
                  backgroundColor: "#333", // Use a dark color for the message bubble background
                }}
              >
                {message.text} - {message.sender}
              </div>
            ))}
          </div>
          <input
            type="text"
            className="input-box"
            id="messageInput"
            placeholder="Type a message..."
            value={inputText}
            onChange={(e) => setInputText(e.target.value)} // Update inputText when input changes
            onKeyDown={handleSendMessage}
          />
          <button className="chat-input" onClick={handleSendMessage}>
            Send
          </button>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
