import React from "react";
import { Link } from "react-router-dom";
import { getDatabase, ref, push } from "https://www.gstatic.com/firebasejs/10.4.0/firebase-database.js";
import { createUserWithEmailAndPassword } from "https://www.gstatic.com/firebasejs/10.4.0/firebase-auth.js";
import { initializeApp } from "https://www.gstatic.com/firebasejs/10.4.0/firebase-app.js";


async function handleSignup() {
  const name = document.getElementById("name").value;
  const lname = document.getElementById("lname").value;
  const phone = document.getElementById("phone").value;
  const email = document.getElementById("email").value;
  const pass = document.getElementById("pass").value;
  const cpass = document.getElementById("cpass").value;

  if (pass !== cpass) {
    alert("Passwords do not match.");
    return;
  }

  try {
    // Create user in Firebase Authentication
    const userCredential = await createUserWithEmailAndPassword(firebaseConfig, email, pass);
    const user = userCredential.user;

    // Store additional user data in the Realtime Database
    const userData = {
      name,
      lname,
      phone,
      email,
    };

    // Push user data to the database
    push(dataRef, userData);

    alert("Signup successful!");
  } catch (error) {
    console.error("Error creating user:", error);
    alert("Signup failed. Please try again.");
  }
}

function Signup() {
  return (
    <div className="center">
      <div className="form-container gradient-bg">
        <h1>Signup Form</h1>
        <div className="form-content">
          <label htmlFor="name">Name</label>
          <input type="text" id="name" />
          <br />
          <br />
          <label htmlFor="lname">Last Name</label>
          <input type="text" id="lname" />
          <br />
          <br />
          <label htmlFor="phone">Phone No.</label>
          <input type="number" id="phone" />
          <br />
          <br />
          <label htmlFor="email">Email</label>
          <input type="email" id="email" />
          <br />
          <br />
          <label htmlFor="pass">Password</label>
          <input type="password" id="pass" />
          <br />
          <br />
          <label htmlFor="cpass">Confirm Password</label>
          <input type="password" id="cpass" />
          <br />
          <button type="submit" className="btn" onClick={handleSignup}>
            Submit
          </button>
          <button type="submit" className="btn" id="login">
            <Link to="/login">Login</Link>
          </button>
        </div>
      </div>
    </div>
  );
}

export default Signup;
