import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./Dash.css";
import { database } from "./firebase";
import { push, ref, onValue, remove } from "firebase/database";

const chatRef = ref(database, "Chat");

function getRandomColor() {
  return "#" + Math.floor(Math.random() * 16777215).toString(16);
}

function Dashboard({ user }) {
  const [inputText, setInputText] = useState("");
  const [messages, setMessages] = useState([]);
  const formatTimestamp = (timestamp) => {
    const date = new Date(timestamp);
    const hours = date.getHours();
    const minutes = date.getMinutes();
    return `${hours}:${minutes < 10 ? "0" : ""}${minutes}`;
  };

  const logout = () => {
    localStorage.clear();
    window.location.reload();
  };

  const handleSendMessage = (e) => {
    if ((e.key === "Enter" || e.type === "click") && inputText.trim() !== "") {
      const userColor = getRandomColor();
      push(chatRef, {
        text: inputText,
        timestamp: Date.now(),
        sender: "Aanchal",
        backgroundColor: userColor,
      }).then(() => {
        setInputText("");
      });
    }
  };
  const handleDeleteMessage = (messageId) => {
    console.log("Deleting message with ID:", messageId);
    const messageRef = ref(database, `Chat/${messageId}`);

    remove(messageRef)
      .then(() => {
        console.log("Message deleted successfully");
      })
      .catch((error) => {
        console.error("Error deleting message:", error);
      });
  };

  useEffect(() => {
    const unsubscribe = onValue(chatRef, (snapshot) => {
      const data = snapshot.val();
      if (data) {
        const messageArray = Object.values(data);
        setMessages(messageArray);
      }
    });
    return () => {
      unsubscribe();
    };
  }, []);

  return (
    <div className="home-container">
      <div id="a1">
        <img
          src="pro.jpg"
          alt=""
          style={{
            height: "20vh",
            width: "10vw",
            borderRadius: "100%",
            position: "fixed",
            top: "3%",
            left: "3%",
          }}
        />
        <div id="a2">
          <button className="chat-logout" onClick={logout}>
            <Link to="/" style={{ color: "white", textDecoration: "none" }}>
              Logout
            </Link>
          </button>
          <div className="chat-box" id="chatBox">
            {messages.map((message, index) => (
              <div
                key={index}
                onClick={() => handleDeleteMessage(index)}
                className="message-box"
                style={{
                  backgroundColor: message.backgroundColor,
                  alignSelf:
                    message.sender === user ? "flex-end" : "flex-start",
                }}
              >

                {message.text}
                <div className="timestamp">
                  {formatTimestamp(message.timestamp)}
                </div>
              </div>
            ))}
          </div>
          <input
            type="text"
            className="input-box"
            id="messageInput"
            placeholder="Type a message..."
            value={inputText}
            onChange={(e) => setInputText(e.target.value)}
            onKeyDown={handleSendMessage}
          />
          <button className="chat-input" onClick={handleSendMessage}>
            Send
          </button>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
