import React, { useState } from "react";
import { Link } from "react-router-dom";
import { getDatabase, ref, push } from "https://www.gstatic.com/firebasejs/10.4.0/firebase-database.js";
import { createUserWithEmailAndPassword } from "https://www.gstatic.com/firebasejs/10.4.0/firebase-auth.js";

const Signup = () => {
  const [formData, setFormData] = useState({
    name: "",
    lname: "",
    phone: "",
    email: "",
    pass: "",
    cpass: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSignup = async () => {
    console.log(formData);
    // if (formData.pass !== formData.cpass) {
    //   alert("Passwords do not match.");
    //   return;
    // }

    // try {
    //   // Create user in Firebase Authentication
    //   const userCredential = await createUserWithEmailAndPassword(firebaseConfig, formData.email, formData.pass);
    //   const user = userCredential.user;

    //   // Store additional user data in the Realtime Database
    //   const userData = {
    //     name: formData.name,
    //     lname: formData.lname,
    //     phone: formData.phone,
    //     email: formData.email,
    //   };

    //   // Push user data to the database
    //   const dataRef = ref(getDatabase(firebaseConfig, databaseURL), "users");
    //   push(dataRef, userData);

    //   alert("Signup successful!");
    // } catch (error) {
    //   console.error("Error creating user:", error);
    //   alert("Signup failed. Please try again.");
    // }
  };

  return (
    <div className="center">
      <div className="form-container gradient-bg">
        <h1>Signup Form</h1>
        <div className="form-content">
          <label htmlFor="name">Name</label>
          <input type="text" id="name" name="name" value={formData.name} onChange={handleChange} />
          <br />
          <br />
          <label htmlFor="lname">Last Name</label>
          <input type="text" id="lname" name="lname" value={formData.lname} onChange={handleChange} />
          <br />
          <br />
          <label htmlFor="phone">Phone No.</label>
          <input type="number" id="phone" name="phone" value={formData.phone} onChange={handleChange} />
          <br />
          <br />
          <label htmlFor="email">Email</label>
          <input type="email" id="email" name="email" value={formData.email} onChange={handleChange} />
          <br />
          <br />
          <label htmlFor="pass">Password</label>
          <input type="password" id="pass" name="pass" value={formData.pass} onChange={handleChange} />
          <br />
          <br />
          <label htmlFor="cpass">Confirm Password</label>
          <input type="password" id="cpass" name="cpass" value={formData.cpass} onChange={handleChange} />
          <br />
          <button type="button" className="btn" onClick={handleSignup}>
            Submit
          </button>
          <button type="button" className="btn" id="login">
            <Link to="/login">Login</Link>
          </button>
        </div>
      </div>
    </div>
  );
};

export default Signup;
