import React, { useState, useEffect } from "react";
import "./Dash.css";
import { ref, push, onChildAdded } from "firebase/database";
import { database } from "./firebase.js";

const chatRef = ref(database, "Chat");

function Dashboard() {
  const [inputText, setInputText] = useState("");
  const [messages, setMessages] = useState([]);
  const [lastSentMessage, setLastSentMessage] = useState(""); // Add this state variable

  const handleSendClick = () => {
    if (inputText.trim() !== "") {
      push(chatRef, {
        text: inputText,
        timestamp: Date.now(),
      });
      setLastSentMessage(inputText); // Update the last sent message
      setInputText("");
    }
  };

  const handleNewMessage = (snapshot) => {
    const newMessage = snapshot.val();
    setMessages((prevMessages) => [newMessage, ...prevMessages]);
  };

  useEffect(() => {
    onChildAdded(chatRef, handleNewMessage);
  }, []);

  return (
    <div className="chat-app-dashboard">
      <div className="blue-portion">
        <center>
          <img
            src="https://cdn3d.iconscout.com/3d/premium/thumb/young-boy-6877456-5638292.png?f=webp"
            alt=""
            style={{
              borderRadius: "100%",
              backgroundColor: "white",
              marginTop: "5%",
            }}
            height={120}
            width={120}
          />
          <h2 style={{ color: "white" }}>Arya</h2>
          <br />
          <hr style={{ color: "violet" }} />
        </center>
      </div>
      <div className="gray-portion">
        <div className="chat-messages">
          {messages.map((message, index) => (
            <div
              key={index}
              style={{
                padding: "2%",
                borderRadius: "20px",
                width:"40%",
                marginBottom:"2%"
              }}
            >
              {message.text}
            </div>
          ))}
        </div>
        <div className="chat-input">
          <input
            type="text"
            placeholder="Type your message..."
            value={inputText}
            onChange={(e) => setInputText(e.target.value)}
          />
          <button onClick={handleSendClick}>Send</button>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
